::-webkit-scrollbar 
{
    width: 4px;
    transition: 2s;
}

::-webkit-scrollbar-thumb 
{
    background: #555;
    border: 0 solid transparent;
    border-radius: 0;
}

::-webkit-scrollbar-track
{
    background: transparent;
    border: 0 solid transparent;
}

::-webkit-scrollbar-corner 
{
    background: transparent;
}

::-webkit-scrollbar-thumb:hover {
    background-color:#888;
}
::-webkit-scrollbar-track:hover {
    width: 8px;
}
::-webkit-scrollbar-thumb:active {
    width: 8px;
}
::-webkit-scrollbar-track:active {
    width: 8px;
}

*{
    color:#EADFD4 !important;
    background-color: #282828 !important;
}

#boards{
    vertical-align:middle;
    font-family: 'liberation sans' !important;
    font-size: 16px;
    font-weight: 100;
}



#logo-fp, #popular-threads, #ft, #abc-cnt{
    display:none !important;
}

body{
    padding: 8% 6% 0% 6%;
    margin:0;
    background: #1d2021 !important;
}

html{
    background-color: #1d2021 !important;

}


.box-outer.top-box{
    border: 2px dashed #4b4b4b !important;
}

#doc
{
    margin:auto;
    padding:0;
    width:100%;
    min-width: auto;
    background-color: #282828 !important;

}

.boxcontent
{
    display: flex;
    flex-direction:row;
    flex-wrap:wrap;  
    justify-content: space-between;
}

.column{
    padding-top:10px;
    margin:0;
}

*{
    filter: none !important;
}

body, html
{
    background-color: #1d2021 !important;
    padding: 0 1% !important;
}

a:link, a:visited
{
   color: #458588 !important;
}

a:hover, a:active
{
   color: #458588 !important;
}


input:focus, input:active{
    outline: none !important;   
}

.sideArrows, #boardNavDesktopFoot, .stylechanger, #toggleMsgBtn, #bannerCnt, #globalMessage, #blotter, #styleSwitcher, #globalToggle, .middlead.center, .abovePostForm, .aboveMidAd, hr, #absbot, .navLinks.navLinksBot.desktop .thread-stats, .ctrl-wrap, .postInfo.desktop input[type=checkbox], .adc-resp-bg, .navLinks.navLinksBottom.mobilebtn, #qrForm input[type=text]
{
    display: none;
}

/* catalog */

#content
{
    width: 100% !important;
}

#content #threads
{
    border: 2px dashed #4b4b4b !important;
    background-color: #282828 !important;
    padding: 20px 0 !important;
    display: flex !important;
    justify-content: space-evenly !important;
    flex-wrap: wrap;
    margin: 0 !important;
}


/* header */

.boardBanner {
    margin: 80px 0 !important;
}

.boardTitle {
    color: #d79921 !important;
    font-weight: 400 !important;
}

#togglePostFormLink a {
    color: #458588 !important;
    font-weight: 400 !important;
}

/* nav links*/

.navLinks.desktop, #ctrl
{
    margin: 20px 0 !important;
}

.navLinks.navLinksBot.desktop
{
    text-align: center !important;
    margin: 0 auto !important;
}


/* thread watcher */

#threadWatcher
{
    margin: 0 !important;
    padding: 0 !important;
    border: 2px dashed #4b4b4b !important;
    background-color: #282828 !important;
}

#watchList li{
    border: none !important;
}

/* OP */

.postContainer.opContainer
{
    display: block !important;
    background-color: #1d2021 !important;
    border: 2px dashed #4b4b4b !important;
    padding: 15px !important;
    margin-bottom: 10px !important;
}

.postContainer.opContainer .fileText{
    width: 100% !important;
    font-size: 12px !important;
    margin: 0 !important;
}

.postContainer.opContainer .fileThumb{
    margin-top: 15px !important;
    margin-bottom: 0 !important;
    margin-right: 0 !important;
    margin-left: 0 !important;
}

.postContainer.opContainer .postInfo.desktop
{
    margin-top: 15px !important;
    margin-bottom: 0 !important;
    margin-right: 0 !important;
    margin-left: 0 !important;
}

.postContainer.opContainer .postMessage
{
    margin-top: 15px !important;
    margin-bottom: 0 !important;
    margin-right: 0 !important;
    margin-left: 0 !important;
}

/* replies */


.reply {
    border: 2px dashed #4b4b4b !important;
    background-color: #282828 !important;
    display: block !important;
    padding: 15px !important;
    margin: 10px auto !important;
}

.postInfo.desktop
{
    width: 100% !important;
    display: inline-block !important;
    font-size: 12px !important;
    padding: 0 !important;
    margin: 0 !important;
}

.file{
    width: 100% !important;
   /* display: inline-block !important; for thumbnails above replies*/
}

.fileText{
    width: 100% !important;
    font-size: 12px !important;
    margin: 15px 0 !important;
}

.fileThumb
{
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    margin-right: 15px !important;
    margin-left: 0 !important;
}

.postMessage
{
    width: 100% !important;
    margin-top: 15px !important;
    margin-bottom: 0 !important;
    margin-right: 0 !important;
    margin-left: 0 !important;
}


.preview {
    border: 2px dashed #4b4b4b !important;
    background-color: #282828 !important;
}

.reply:target, .reply.highlight{
    border: 2px dashed #4b4b4b !important;
    background-color: #1d2021 !important;

}

.quote
{
    color: #98971a !important;
}

/* quick reply */

#quickReply
{
    border: 2px solid #4b4b4b !important;
    padding: 0 !important;
    margin: 0 !important;
    overflow: hidden !important;
    background-color: #222222 !important;
}

#qrHeader
{
    font-size: 10px !important;
    font-family: monospace;
    margin: 9px 0 !important;
    line-height: 2;
    background-color: #222222 !important;
}

#qrHeader img{
    position: absolute;
    right: 10px;
}

#qrCaptchaContainer
{
    height: 0 !important;
    margin: 0 auto !important;
    overflow: hidden !important;
}

#qrCaptchaContainer div{
    display: block !important;
    position: absolute !important;
    height: 55px !important;
    width: 41px !important;
    top: -9px !Important;
    right: 2px;
    left: -4px !important;
    overflow: hidden !important;

}

#qrForm{
    margin: 0 !important;
    padding: 0 !important;

}

#qrForm div
{
    border: none !important;
    margin: 0 !important;
    padding: 0 !important;

}

