#qrForm textarea
{
    text-align: left;
    Border: none !important;
    background-color: #282828 !important;
    margin: 0 0 0 0 !important;
    padding: 0 !important;
}


#qrForm input[type=file]
{
    width: 124%!important;
    margin: 0 0 0 0 !important;
    display: inline-block !important;
    border: 0 dashed #4b4b4b !important;
    background-color: #1d2021 !important;
    font-size: 10px !important;
    font-family: monospace;
    left: -72px !important;
    position: relative;
    border-top: 2px dashed #00000000 !important;
    border-bottom: 2px dashed #00000000 !important;
    border-left: none !important;
    border-right: none !important;
    height: 20px !important;
}

#qrSpoiler
{
    width: 100% !important;
    display: block !important;
    font-size: 10px !important;
    font-family: monospace;
}

#qrForm input[type=submit]
{
    font-size: 10px !important;
    font-family: monospace;
    padding: 0 !important;
    margin: 0 0 0 0 !important;
    background-color: #1d2021 !important;
    display: inline-block !important;
    width: 100% !important;
    border-top: 2px dashed #00000000 !important;
    border-bottom: 2px dashed #00000000 !important;
    border-left: none !important;
    border-right: none !important;  
    height: 25px !important;
    color:#ebdbb2 !important;
}

#qrError
{
    margin: 0 auto !important;
    min-width: 0 !important;
}

#qrForm input[type=submit]:focus,
#qrForm input[type=submit]:active,
#qrForm input[type=file]:focus,
#qrForm input[type=file]:active
{
    border-top: 2px dashed #4d4d4d !important;
    border-bottom: 2px dashed #4d4d4d !important;
}


/* post a reply (bottom)*/

.navLinks.navLinksBot.desktop .open-qr-wrap
{
    position: inherit !important;
    margin-top: 20px !important;
    margin-bottom: 15px !important;
    margin-right: auto !important;
    margin-left: auto !important;  
    padding: 0 !important;
}


/* delete post */

.bottomCtrl.desktop
{
    margin: 15px 0 !important;
    padding: 0 !important;
    width: 100% !important;
    text-align: center !important;
}

.bottomCtrl.desktop input[type=submit]
{
    display: inline !important;
    border: 2px dashed #4b4b4b !important;
    background-color: #1d2021;
    margin-left: 4px !important;
    color: inherit !important;
}

.bottomCtrl.desktop input[type=submit]:active,
.bottomCtrl.desktop input[type=submit]:active
{
    border: 2px dashed #ebdbb2 !important;
}
